format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">				<h2>Lab Test<br><small>{{ doc.name }}</small>				</h2></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class="print-heading" style="padding-bottom: 5px;margin-bottom: 5px;border-bottom: 0px solid #d1d8dd;">
    <h1 class="text-center"><strong>Lab Test Results</strong></h1>
</div>

<div class="row section-break" style="padding-bottom: 0px;margin-bottom: 0px;border-bottom: 0px solid #d1d8dd; margin-top: 0px; margin-bottom: 0px; border: 0;border-top: 0px solid #d1d8dd;">
    <div class="col-xs-6 column-break">
      <div class="row">
        <div class="col-xs-4 text-left">
          <label>Patient</label>
        </div>
        {% if doc.patient_name %}
        <div class="col-xs-7  value">
          {{ doc.patient_name }}
        </div>
        {% else %}
        <div class="col-xs-7  value">
          {{ doc.patient }}
        </div>
        {%- endif -%}
      </div>

      <div class="row">
            <div class="col-xs-4 text-left">
              <label>Age</label>
            </div>
            <div class="col-xs-7  value">
                {% set dob = frappe.db.get_value("Patient", doc.patient, ['dob'])  %}
                 {% set now  = frappe.utils.nowdate() %}
                 {% set diff = frappe.utils.date_diff(now, dob) %}
                    {{ diff//365 }} years
            </div>
      </div>

      <div class="row">
        <div class="col-xs-4 text-left">
          <label>Gender</label>
        </div>
        {% if doc.patient_sex %}
            <div class="col-xs-7  value">
              {{ doc.patient_sex }}
            </div>
        {% endif %}
      </div>

    </div>

    <div class="col-xs-6 column-break">

      <div class="row">

        <div class="col-xs-4 text-left">
          <label>Practitioner</label>
        </div>
        {% if doc.practitioner_name %}
        <div class="col-xs-7  text-left value">
          {{ doc.practitioner_name }}
        </div>
        {% else %}
			{% if doc.referring_practitioner_name %}
            <div class="col-xs-7  text-left value">
            {{ doc.referring_practitioner_name }}
            </div>
		    {% endif %}
        {%- endif -%}
      </div>
      <div class="row">
          <div class="col-xs-4 text-left">
            <label>Result Date</label>
            </div>
            {% if doc.result_date %}
                <div class="col-xs-7  text-left value">
                  {{ doc.result_date }}
                </div>
          {% endif %}
      </div>
    </div>
  </div><hr>

<div style="margin-top: 0px; margin-bottom: 0px; border: 0;border-top: 0px solid #d1d8dd; border-top: 0px solid #d1d8dd;">
    {% set tests = frappe.db.get_all("Lab Test", {'ref_docname': frappe.db.get_value("Patient Encounter",doc.ref_docname, ['name'])}, ['name']) %}
    {% for test in tests %}
    	{% set depart = frappe.db.get_value("Lab Test", test, ['department']) %}
    	{% set test_name = frappe.db.get_value("Lab Test", test, ['lab_test_name']) %}
        {% set normal_results = frappe.get_all("Normal Test Result", {'parent': test.name}, ['lab_test_name','lab_test_event', 'result_value', 'normal_range']) %}
        {% set descriptive_results = frappe.get_all("Descriptive Test Result", {'parent': test.name}, ['lab_test_particulars', 'result_component_option', 'require_result_value']) %}
        {% set organism_results = frappe.get_all("Organism Test Result", {'parent': test.name}, ['organism', 'colony_population', 'colony_uom']) %}
        {% set sensitivity_results = frappe.get_all("Sensitivity Test Result", {'parent': test.name}, ['antibiotic', 'antibiotic_sensitivity']) %}

        <h3 class="text-uppercase text-center" style="margin-top: 0px; margin-bottom: 0px;padding: 0px !important;"><b><u>Department of {{ depart }}</u></b></h3>
        <h6 class="text-center" style="padding-bottom: 0px;margin-top: 5px; margin-bottom: 0px;padding: 0px !important;">Test Name:<u style="text-transform:uppercase"><strong>{{ test_name }}</strong></u></h6>


        <table class="table" style="padding-bottom: 8px;margin-top: 0px; margin-bottom: 8px;padding: 0px !important;">
            <tbody style="width: 100%; height: auto;">
                {% if normal_results %}
                  <tr>
                    <th class="text-left" style="width: 35%;">Name of Test</th>
                    <th class="text-left" style="width: 35%;">Result</th>
                     <th class="text-left" style="width: 35%;">Normal Range</th>
                  </tr>
                  {% for row in normal_results %}
                      <tr>
                        <td style="width:35%;text-align: left;border:none; padding: 0px !important;">
                           {% if row.lab_test_name %}<b>{{ row.lab_test_name }}</b>{%endif%}
                        </td>
                        <td style="width:35%;text-align: left;border:none; padding: 0px !important;">
                            {% if row.result_value %}{{ row.result_value }}{%endif%}
                        </td>
                        <td style="width: 35%;text-align: left;border:none; padding: 0px !important;">
                            {% if row.normal_range %}{{ row.normal_range }}{%endif%}
                        </td>
                      </tr>
                     {% endfor %}
              {% endif %}
            </tbody>
        </table>

        <table class="table" style="padding-bottom: 8px;margin-top: 0px; margin-bottom: 8px;padding: 0px !important;">
          <tbody style="width: 100%; height: auto;">
               {% if descriptive_results %}
                    <tr>
                        <th style="width: 40%;text-align: left;border:none;">Name of Test</th>
                        <th style="width: 20%;text-align: center;border:none;"></th>
                        <th style="width: 40%;text-align: left;border:none;">Result Component Option</th>
                    </tr>
                    {% for row in descriptive_results %}
                     <tr>
                         <td style="width: 40%;text-align: left;border:none;padding: 0px !important;">
                             {% if row.lab_test_particulars %}<b>{{ row.lab_test_particulars }}</b>{%endif%}
                        </td>
                         <td style="width: 20%;text-align: center;border:none;padding: 0px !important;">
                             <!--{% if row.result_value %}{{ row.result_value}}{%endif%}-->
                        </td>
                         <td style="width: 40%;text-align: left;border:none;padding: 0px !important;">
                             {% if row.result_component_option %}{{ row.result_component_option }}{%endif%}
                        </td>
                     </tr>   
                  {% endfor %}
                {% endif %}
          </tbody>
        </table>

        <table class="table" style="padding-bottom: 8px;margin-top: 0px; margin-bottom: 8px;padding: 0px !important;">
          <tbody style="width: 100%; height: auto;">
               {% if organism_results %}
                    <tr>
                        <th class="text-left">Name of Organism</th>
                        <th class="text-center">Colony Populataion</th>
                        <th class="text-center">Colony UOM</th>
                    </tr>
                    {% for row in organism_results %}
                        <tr>
                            <td style="width: 40%;text-align: left;border:none;padding: 0px !important;">
                                 {% if row.organism %}<b>{{ row.organism }}</b>{%endif%}
                            </td>
                            <td style="width: 25%;text-align: center;border:none;padding: 0px !important;">
                                 {% if row.colony_population %}{{ row.colony_population }}{%endif%}
                            </td>
                            <td style="width: 35%;text-align: center;border:none;padding: 0px !important;">
                                 {% if row.colony_uom %}{{ row. colony_uom}}{%endif%}
                            </td>
                        </tr>   
                    {% endfor %}
                {% endif %}
          </tbody>
        </table>

        <table class="table" style="padding-bottom: 8px;margin-top: 0px; margin-bottom: 8px;padding: 0px !important;">
            <tbody style="width: 100%; height: auto;">
                {% if sensitivity_results %}
                <tr>
                    <th class="text-left">Name of Antibiotic</th>
                    <th class="text-center"></th>
                    <th class="text-center">Sensitivity</th>
                </tr>
                {% for row in sensitivity_results %}
                    <tr>
                        <td style="width: 30%;text-align: left;border:none;padding: 0px !important;">
                              {% if row.antibiotic %}<b>{{ row.antibiotic }}</b>{%endif%}
                        </td>
                         <td style="width: 20%;text-align: center;border:none;padding: 0px !important;"></td>
                        <td style="width: 45%;text-align: center;border:none;padding: 0px !important;">
                             {% if row.antibiotic_sensitivity %}{{ row. antibiotic_sensitivity}}{%endif%}
                        </td>
                    </tr>   
                {% endfor %}
                {% endif %}
            </tbody>
        </table>
    {% endfor %}
</div>"}]
