.print-format {
    padding: 0.5in;
}

h1 {
    margin-top: 5px;
    margin-bottom: 5px;
}

.print-format td, .print-format th {
    vertical-align: top !important;
    padding: 0px !important;
}

.table {
    margin-top: 5px;
    margin-bottom: 5px;
}

