								<td class="text-left" style="border:none;">
								   <small><b>{{ frappe.db.get_value('User', row.modified_by, 'full_name') }}</b></small>
								</td>
							  </tr>
							{% endif %}
						  {% endif %}
						{% endfor %}
			
					  {% else %}
						<h3><strong>{{ depart }}</strong></h3> 
						<b>{{ test_name }}</b>
						<tr>
						  <th class="text-left" style="border:none;">Name of Test</th>
						  <th class="text-left" style="border:none;">Result</th>
						  <th class="text-left" style="border:none;">Status</th>
						  <th class="text-left" style="border:none;">Normal Range</th>
						  <th class="text-left" style="border:none;">Reviewed By</th>
						</tr>
						{% for row in normal_results %}
						  {% if ( row.result_status=="N" or row.result_status==" ") %}
							<tr>
							  <td class="text-left" style="border:none;">
								{% if row.lab_test_name %}{{ row.lab_test_name }}{%endif%}
							  </td>
							  <td class="text-left" style="border:none;">
								{% if row.result_value %}{{ row.result_value }}{%endif%}
