					 <colgroup>
					  <col width="20%">
					  <col width="32%">
					  <col width="32%">
					  <col width="16%"> 
					</colgroup>
					<tbody>
					  {% if depart != var['old'] %}
					  <h3><strong>{{ depart }}</strong></h3>
						<tr>
						  <th class="text-left" style="border:none;">Name of Test</th>
						  <th class="text-left" style="border:none;">Result Component</th>
						  <th class="text-left" style="border:none;">Result Value</th>
						  <th class="text-left" style="border:none;">Reviewed By</th>
						</tr>   
					  {% endif %}  
					  {% set d = var.update({'old': depart }) %} 
					  {% for row in descriptive_results %}
						<tr>
						  <td class="text-left" style="border:none;">
							{% if row.lab_test_particulars %}{{ row.lab_test_particulars }}{%endif%}
						  </td>
						  <td class="text-left" style="border:none;">
							{% if row.result_component_option %}
							  {{ row.result_component_option }}
							{% endif%} 
						  </td>
						  <td class="text-left" style="border:none;">
							{% if row.result_value %}
							  {{ row.result_value }}
							{%endif%}
						  </td>
						  <td class="text-left" style="border:none;">
							<small>{{ frappe.db.get_value('User', row.modified_by, 'full_name') }}</small>
						   </td>
						</tr>
						{% if d %}
						   <tr>
							<td class="text-left" style="border:none;">
							  {% if row.lab_test_particulars %}{{ row.lab_test_particulars }}{%endif%}
