							<td class="text-left" style="border:none;"></td>
							<td class="text-left" style="border:none;">
							  {% if row.result_component_option %}
								{{ row.result_component_option }}
							  {% else %}
								{{ row.result_value }}
							  {%endif%}
							</td>
							<td class="text-left" style="border:none;"></td>
							<td class="text-left" style="border:none;">
							  <small>{{ frappe.db.get_value('User', row.modified_by, 'full_name') }}</small>
						   </td>
						  </tr>
						{% endif %}
					  {% endfor %}
					</tbody>
				  </table>
					{% endif %}
				
					{% if organism_results %}
						<table class="table">
					  <colgroup>
					  <col width="30%">
					  <col width="12%">
					  <col width="12%">  
					  <col width="30%">  
					  <col width="16%">  
					</colgroup>
					<tbody>
				   {% if depart != var['old'] %}
					 <h3><strong>{{ depart }}</strong></h3>
					  <tr>
						<th class="text-left" colspan=2>Name of Organism</th>
						<th class="text-center">Colony Populataion</th>
						<th class="text-center">Colony UOM</th>
						<th class="text-left" style="border:none;">Reviewed By</th>
					  </tr>
					  {% endif %}  
					  {% set d = var.update({'old': depart }) %}
					  {% for row in organism_results %}
						<tr>
						  <td class="text-left" style="border:none;" colspan=2>
							 {% if row.organism %}<b>{{ row.organism }}</b>{%endif%}
						  </td>
						  <td class="text-center" style="border:none;">
							 {% if row.colony_population %}{{ row.colony_population }}{%endif%}
						  </td>
						  <td class="text-center" style="border:none;">
							 {% if row.colony_uom %}{{ row. colony_uom}}{%endif%}
						  </td>
						  <td class="text-left" style="border:none;">
							<small>{{ frappe.db.get_value('User', row.modified_by, 'full_name') }}</small>
						   </td>
						</tr>  
						{% if d %}
						  <tr>
							<td class="text-left" style="border:none;" colspan=2>
							   {% if row.organism %}<b>{{ row.organism }}</b>{%endif%}
							</td>
							<td class="text-center" style="border:none;">
							   {% if row.colony_population %}{{ row.colony_population }}{%endif%}
							</td>
							<td class="text-center" style="border:none;">
							   {% if row.colony_uom %}{{ row. colony_uom}}{%endif%}
							</td>
							<td class="text-left" style="border:none;">
							  <small>{{ frappe.db.get_value('User', row.modified_by, 'full_name') }}</small>
							 </td>
						  </tr>  
						{% endif %}
					  {% endfor %}
					</tbody>
				  </table>
					{% endif %}
				
					{% if sensitivity_results %}
						<table class="table">
					<colgroup>
					  <col width="30%">
					  <col width="12%">
					  <col width="12%">  
					  <col width="30%">  
					  <col width="16%">  
					</colgroup>
				  <tbody>
				 {% if depart != var['old'] %}
				   <h3><strong>{{ depart }}</strong></h3>
					<tr>
					  <th class="text-left" colspan=2>Name of Antibiotic</th>
					  <th class="text-center" colspan=2>Sensitivity</th>
					  <th class="text-left" style="border:none;">Reviewed By</th>
					</tr>
					 {% endif %}  
					{% set d = var.update({'old': depart }) %}
					{% for row in sensitivity_results %}
					  <tr>
						<td class="text-left" style="border:none;" colspan=2>
							{% if row.antibiotic %}<b>{{ row.antibiotic }}</b>{%endif%}
						</td>
						<td class="text-center" style="border:none;" colspan=2>
						   {% if row.antibiotic_sensitivity %}{{ row.antibiotic_sensitivity}}{%endif%}
						</td>
						<td class="text-left" style="border:none;">
						  <small>{{ frappe.db.get_value('User', row.modified_by, 'full_name') }}</small>
						 </td>
					  </tr>   
					  {% if d %}
						 <tr>
						  <td class="text-left" style="border:none;" colspan=2>
							  {% if row.antibiotic %}<b>{{ row.antibiotic }}</b>{%endif%}
						  </td>
						  <td class="text-center" style="border:none;" colspan=2>
							 {% if row.antibiotic_sensitivity %}{{ row.antibiotic_sensitivity}}{%endif%}
						  </td>
